/* eslint-disable */
import { Case } from "src/v3/root/types/case";

export interface CaseHierarchy {

  /**
   * Name of the virtual folder generated by the hierarchy descriptor, should be undefined if its the root'.
   */
  levelName?: string;

  /**
   * 'true' if this is the last level of hierarchy, and the items is Case[].
   */
  lastLevel: boolean;

  /**
   * Items that are contained in this level.
   */
  // TODO: Question for Jirka, should it be allowed to have (CaseHierarchy| Case)[] instead? 
  // is there a use case for this? mixing cases/"folders" on the same level?
  items: CaseHierarchy[] | Case[];
}
